import React from 'react'
import './Card.css'
const Card = ({id, title, image, description}) => {
	return(
		<div>
			<div className="dib tc bg--light-green br3 grow">
				<img
					className=""
					src= {image}
					alt='img'/>
				<div className="">
					<h2>{title}</h2>
					<p >{description}</p>
				</div>
			</div>
		</div>
	);
}

export default Card;
