import React from 'react'
import './Card.css'
const Card = ({id, title, image, description}) => {
	return(
		<div>
			<div className="img-container">
				<img
					className="dib bg--light-green br3 pa3 grow shadow-2"
					src= {image}
					alt='img'
				/>
			</div>
			<div>
				<h2>{title}</h2>
				<p>{description}</p>
			</div>
		</div>
	);
}

export default Card;
